import React, { Component } from 'react'

export class Sidebar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categories: [
        {
          id: 1,
          name: 'Drama',
          movies: [
            { id: 1, title: '127 hours', released: 2010, embedId: ''},
            { 
              id: 2, 
              title: 'A última música', 
              released: 2010, 
              embedId: ''
            },
            { id: 3, title: 'Ataque dos cães', released: 2021, embedId: ''}
          ]
        },
        {
          id: 2,
          name: 'Ação',
          movies: [
            { id: 1, title: 'Homem aranha', released: 2021, embedId: ''},
            { 
              id: 2, 
              title: 'Maze Runner', 
              released: 2014, 
              embedId: ''
            },
            { id: 3, title: 'A guerra do amanhã', released: 2021, embedId: ''}
          ]
        },
        {
          id: 3,
          name: 'Comédia',
          movies: [
            { id: 1, title: 'Ron bugado', released: 2022, embedId: ''},
            { 
              id: 2, 
              title: 'A era do gelo', 
              released: 2022, 
              embedId: ''
            },
            { id: 3, title: 'Zootopia', released: 2016, embedId: ''}
          ]
        }
      ]
    }
  }

  render() {
    const { categories } = this.state;
    return (
      <div>
        <h2>Categorias</h2>
        {
          categories.map((category) => (
            <div key={ category.id }>
              <h3> { category.name } </h3>
              <ul>
                {
                  category.movies.map((movie) => (
                    <li key={ movie.id }>
                      { movie.title } was released in { movie.released }
                      <button type='button' onClick={ () => {} }>
                        Assistir
                      </button>
                    </li>
                  ))
                }
              </ul>
            </div>
          ))
        }
      </div>
    )
  }
}

export default Sidebar;
